mapper.select:
select course_name
from tb_course
where course_id = #{courseId}

select course_id, course_name, weekday, `time`, teacher_name, course_room
from tb_course inner join tb_teacher tt on tb_course.teacher_id = tt.teacher_id


        course_name = #{courseName}


        and `time` = #{courseTime}


        and teacherName = #{teacherName}
