mapper.select:
select ts.student_id, student_name, ts.course_id, course_name, usual_grade, final_grade, total_grade from tb_score ts inner join tb_course tc on ts.course_id = tc.course_id inner join tb_teacher tt on tc.teacher_id = tt.teacher_id inner join tb_student t on ts.student_id = t.student_id where ts.course_id = #{courseId} and tc.teacher_id = #{teacherId}
and ts.student_id = #{studentId}
and student_name like concat('%', #{studentName}, '%')

select tb_select.course_id, course_name, tc.teacher_id, teacher_name,ts.student_id, student_name, weekday, course_room, `time`, offer_state
from tb_select
inner join tb_student ts on tb_select.student_id = ts.student_id
inner join tb_course tc on tb_select.course_id = tc.course_id
inner join tb_teacher tt on tc.teacher_id = tt.teacher_id


        tb_select.course_id = #{courseId}


        and tc.teacher_id = #{teacherId}


        and student_id = #{studentId}


        and teacher_name like concat('%',#{teacherName},'%')


        and course_name like concat('%',#{courseName},'%')


        and student_name like concat('%',#{studentName},'%')


        and weekday = #{weekday}


        and `time` = #{time}
